import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

const ChooseButton = ({ paroisse }) => {
  const [isChoosen, setIsChoosen] = useState(false);

  const dispatch = useDispatch();
  const user = useSelector((state) => state.user.user);
  const Uid = user._id;
  const Pid = paroisse._id;

  const toggleBtn = () => {
    console.log(Uid);
    console.log(Pid);
  };
  return (
    <div>
      {Uid && isChoosen === true && (
        <button className="choose-paroisse-container" onClick={toggleBtn}>
          <p>Paroisse déjà choisie</p>{" "}
          <img
            src={process.env.PUBLIC_URL + "/imgs/icons/love.png"}
            alt="heart"
          />
        </button>
      )}

      {Uid && isChoosen === false && (
        <button className="choose-paroisse-container" onClick={toggleBtn}>
          <p>Choisir comme ma paroisse</p>{" "}
          <img
            src={process.env.PUBLIC_URL + "/imgs/icons/love.png"}
            alt="heart"
          />
        </button>
      )}
    </div>
  );
};

export default ChooseButton;
