import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllParoisse } from "../../actions/paroisse";
import { isEmpty } from "../utils/index";
import { NavLink } from "react-router-dom";
import { useState } from "react";

import Select from "react-select";
import makeAnimated from "react-select/animated";

const animatedComponents = makeAnimated();

const ParoisseBoard = () => {
  // Jours - Heures / Messes & Confessions
  const [days, setDays] = useState([
    {
      id: 1,
      dayName: "Lundi",
      dayHour: [],
    },
    {
      id: 2,
      dayName: "Mardi",
      dayHour: [],
    },
    {
      id: 3,
      dayName: "Mercredi",
      dayHour: [],
    },
    {
      id: 4,
      dayName: "Jeudi",
      dayHour: [],
    },
    {
      id: 5,
      dayName: "Vendredi",
      dayHour: [],
    },
    {
      id: 6,
      dayName: "Samedi",
      dayHour: [],
    },
    {
      id: 7,
      dayName: "Dimanche",
      dayHour: [],
    },
  ]);
  const [daysConf, setDaysConf] = useState([
    {
      id: 1,
      dayName: "Lundi",
      dayHour: [],
    },
    {
      id: 2,
      dayName: "Mardi",
      dayHour: [],
    },
    {
      id: 3,
      dayName: "Mercredi",
      dayHour: [],
    },
    {
      id: 4,
      dayName: "Jeudi",
      dayHour: [],
    },
    {
      id: 5,
      dayName: "Vendredi",
      dayHour: [],
    },
    {
      id: 6,
      dayName: "Samedi",
      dayHour: [],
    },
    {
      id: 7,
      dayName: "Dimanche",
      dayHour: [],
    },
  ]);

  // Données formulaire
  const [formData, setFormData] = useState({
    name: "",
    province: "",
    diocese: "",
    address: "",
    contact: "",
    email: "",
    history: "",
    pictures: [],
    location: {
      type: "Point",
      coordinates: [null, null],
    },
    messes: [],
    confessions: [],
  });

  return (
    <div className="mod-paroisse-container">
      <h1>Ma Paroisse</h1>

      <div className="mod-paroisse-form">
        <form onSubmit={(e) => submitForm(e)}>
          <div className="form-group">
            <label htmlFor="name">Nom</label>
            <input
              htmlFor="name"
              placeholder="Nom"
              value={formData.name}
              onChange={(e) =>
                setFormData({ ...formData, name: e.target.value })
              }
            />
          </div>
          <div className="form-group-group">
            <div className="form-group">
              <label htmlFor="province">Province</label>
              <input
                htmlFor="province"
                placeholder="Province"
                value={formData.province}
                onChange={(e) =>
                  setFormData({ ...formData, province: e.target.value })
                }
              />
            </div>
            <div className="form-group">
              <label htmlFor="diocese">Diocèse</label>
              <input
                htmlFor="diocese"
                placeholder="Diocèse"
                value={formData.diocese}
                onChange={(e) =>
                  setFormData({ ...formData, diocese: e.target.value })
                }
              />
            </div>
          </div>
          <div className="form-group-group">
            <div className="form-group">
              <label htmlFor="address">Adresse</label>
              <input
                htmlFor="address"
                placeholder="Adresse"
                value={formData.address}
                onChange={(e) =>
                  setFormData({ ...formData, address: e.target.value })
                }
              />
            </div>
            <div className="form-group">
              <label htmlFor="contact">Contact</label>
              <input
                htmlFor="contact"
                placeholder="Contact"
                value={formData.contact}
                onChange={(e) =>
                  setFormData({ ...formData, contact: e.target.value })
                }
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                htmlFor="email"
                placeholder="Email"
                value={formData.email}
                onChange={(e) =>
                  setFormData({ ...formData, email: e.target.value })
                }
              />{" "}
            </div>
          </div>
          <div className="form-group">
            <label htmlFor="history">Histoire</label>
            <textarea
              htmlFor="history"
              placeholder="Histoire"
              value={formData.history}
              onChange={(e) =>
                setFormData({ ...formData, history: e.target.value })
              }
            />
          </div>

          {/** Programmes */}
          <div className="programmes">
            <div className="programmes-title">
              <h1>Programmes</h1>
            </div>

            <div className="programme">
              <div className="programme-title">
                <h3>Programme des messes</h3>
              </div>
              <div className="programme-content">
                {days.map((day, idx) => (
                  <div className="day-block">
                    <h3 className="day-title">{day.dayName}</h3>
                    <Select
                      className="select-element"
                      options={dayOptions}
                      closeMenuOnSelect={false}
                      components={animatedComponents}
                      isMulti
                      onChange={(e) => handleSelectMesses(e, day.id)}
                      defaultValue={isEdit && renderArrFinal(arrFinal, idx)}
                    />
                  </div>
                ))}
              </div>
            </div>

            <div className="programme">
              <div className="programme-title">
                <h3>Programme des Confessions</h3>
              </div>
              <div className="programme-content">
                {days.map((day, idx) => (
                  <div className="day-block">
                    <h3 className="day-title">{day.dayName}</h3>
                    <Select
                      className="select-element"
                      options={dayOptions}
                      closeMenuOnSelect={false}
                      components={animatedComponents}
                      isMulti
                      onChange={(e) => handleSelectConfessions(e, day.id)}
                      defaultValue={isEdit && renderArrFinal(arrConfFinal, idx)}
                    />
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/** Pictures */}
          <div className="pictures">
            <div className="pictures-title">
              <h3>Pictures</h3>
            </div>
            <div className="pictures-content">
              <div className="pictures-blocks-container">
                {!isEmpty(files) && <>{renderFiles()}</>}

                <div className="file-upload">
                  <input
                    type="file"
                    name="file"
                    onChange={(e) => fileSelectedHandler(e)}
                    accept=".jpg, .jpeg, .png"
                  />
                  <i className="fa fa-arrow-up"></i>
                </div>
              </div>
            </div>
          </div>
          {/** Clergy */}

          <div className="btn-group">
            <input type="submit" value="Save" />
            {isOk === true && <i className="fa fa-spinner fa-spin"></i>}

            <input type="reset" value="Annuler" onClick={() => cancelForm()} />
          </div>
        </form>
      </div>
    </div>
  );
};

export default ParoisseBoard;
