import React, { useEffect } from "react";

// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Autoplay, Keyboard, Pagination } from "swiper";

// Import Swiper styles
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";

// Import Redux Modules
import { useSelector, useDispatch } from "react-redux";

// Import events actions
import { getFirstThreeEvents } from "../../actions/event";

const Carousel = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    const events = dispatch(getFirstThreeEvents());
  }, []);

  return (
    <div>
      <Swiper
        spaceBetween={30}
        centeredSlides={true}
        autoplay={{
          delay: 3000,
          disableOnInteraction: false,
        }}
        pagination={{
          clickable: true,
        }}
        navigation={true}
        keyboard={true}
        modules={[Autoplay, Pagination, Navigation, Keyboard]}
      >
        <SwiperSlide>
          <img
            src="./uploads/events/default-event.jpg"
            alt="event"
            width="1310vh"
          />
        </SwiperSlide>
        <SwiperSlide>
          <img
            src="./uploads/events/default-event.jpg"
            alt="event"
            width="1310vh"
          />
        </SwiperSlide>
        <SwiperSlide>
          <img
            src="./uploads/events/default-event.jpg"
            alt="event"
            width="1310vh"
          />
        </SwiperSlide>
      </Swiper>
    </div>
  );
};

export default Carousel;
