import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { login, signup } from "../../../actions/auth";
import { GoogleLogin } from "@react-oauth/google";
import jwtDecode from "jwt-decode";

const SignInForm = () => {
  const [userData, setUserData] = useState({
    email: "",
    password: "",
  });

  const [googleUser, setGoogleUser] = useState({
    email: "",
    password: "",
    name: "",
    lastName: "",
    confirmPassword: "",
  });

  const dispatch = useDispatch();
  const { message } = useSelector((state) => state.message);

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch(login(userData));
  };

  const googleSuccess = async (res) => {
    const decoded = jwtDecode(res.credential);
    console.log("================CREDENTIALS====================");
    console.log(decoded);
    console.log("================CREDENTIALS====================");

    setUserData({ ...userData, email: decoded?.email, password: decoded?.sub });
    setGoogleUser({
      ...googleUser,
      email: decoded?.email,
      password: decoded?.sub,
      confirmPassword: decoded?.sub,
      name: decoded?.given_name,
      lastName: decoded?.family_name,
    });
    //111256468757037941212
    dispatch(login(userData)).catch((err) => {
      console.log(err);
      dispatch(signup(googleUser));

    });
  };
  const googleFailure = (error) => {
    console.log("====================================");
    console.log(error);
    console.log("====================================");

    console.log("Google Sign In was unsuccessful. Try Again later.");
  };

  return (
    <div className="sign-in-container">
      <div className="header-container">
        <h1>Heureux de vous revoir !</h1>
        <p>
          Lorem, ipsum dolor sit amet consectetur adipisicing elit. Quidem quas
          maiores nisi.
        </p>
      </div>
      <form action="" onSubmit={handleSubmit}>
        <div className="inputs-group">
          <label htmlFor="email">Email</label>
          <br />
          <input
            type="email"
            required
            name="email"
            id="email"
            className="input-form"
            value={userData.email}
            onChange={(e) =>
              setUserData({ ...userData, email: e.target.value })
            }
          />
        </div>
        <div className="inputs-group">
          <label htmlFor="password">Mot de Passe</label>
          <br />
          <input
            type="password"
            required
            name="password"
            id="password"
            className="input-form"
            value={userData.password}
            onChange={(e) =>
              setUserData({ ...userData, password: e.target.value })
            }
          />
        </div>
        <div className="inputs-group">
          <input type="submit" value="Connexion" />
        </div>
        <div>
          <p className="error">{message}</p>
        </div>

        <GoogleLogin
          //clientId={GOOGLE_CLIENT_ID}
          onSuccess={googleSuccess}
          onError={googleFailure}
          //cookiePolicy="single_host_origin"
        />
      </form>
    </div>
  );
};

export default SignInForm;
