import React from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { NavLink, useParams } from "react-router-dom";
import SearchBar from "../components/Search/SearchBar";
import NavBar from "../components/NavBar/NavBar";

const Diocese = (props) => {
  const { name } = useParams();

  const paroisses = useSelector((state) => state.paroisses);

  useEffect(() => {
    console.log("====================================");
    console.log(paroisses);
    console.log("====================================");
  }, [paroisses]);

  const sendName = () => {
    const voy = ["a", "e", "i", "o", "u", "A", "E", "I", "O", "U"];
    let bool = true;

    for (let i = 0; i < voy.length; i++) {
      if (name[0] === voy[i]) {
        bool = true;
        break;
      } else {
        bool = false;
      }
    }

    return bool === true ? `Diocèse d'${name}` : `Diocèse de ${name}`;
  };

  const sendProvinceName = () => {
    const voy = ["a", "e", "i", "o", "u", "A", "E", "I", "O", "U"];
    let bool = true;

    for (let i = 0; i < voy.length; i++) {
      if (name[0] === voy[i]) {
        bool = true;
        break;
      } else {
        bool = false;
      }
    }

    return bool === true ? `d'${name}` : `de ${name}`;
  };

  return (
    <>
      <NavBar value={"Paroisses"} />
      <SearchBar />
      <div className="">
        <div className="file-arianne">
          <NavLink to="/provinces" className="file-ariane-element">
            <h1>Diocèses et Paroisses</h1>
          </NavLink>
          <NavLink to="/provinces" className="file-ariane-element">
            <h1>Provinces écclésiastique {sendProvinceName()}</h1>
          </NavLink>
          <div className="file-ariane-element">
            <h1>{sendName()}</h1>
          </div>
        </div>

        <div className="table-container">
          <div className="table-header">
            <div>Nom</div>
            <div>Adresse Postale</div>
            <div>Contact</div>
            <div>Email</div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Diocese;
