import React, { useState, useContext } from "react";
import { AdminContext } from "../../pages/AdminDashboard";

const LeftBar = ({ userName }) => {
  const [isActive, setIsActive] = useState(1);
  const { menuId, setMenuId } = useContext(AdminContext);

  const toggleActive = (e) => {
    const elements = document.querySelectorAll(".left-bar-menu-element");
    setMenuId(e.dataset.key);
    setIsActive(e.dataset.key);

    console.log("====================================");
    console.log(menuId);
    console.log("====================================");

    elements.forEach((e) => {
      e.classList.remove("active");
    });
    e.classList.add("active");
  };
  return (
    <div className="left-bar-container">
      <div className="left-bar-header">
        <img
          src={process.env.PUBLIC_URL + "/imgs/icons/church.png"}
          alt={"logo"}
        />
        <div>
          <h4>{userName}</h4>
          <h6>Administrateur</h6>
        </div>
      </div>

      <div className="left-bar-menu-container">
        <div
          className="left-bar-menu-element active"
          onClick={(e) => toggleActive(e.target)}
          data-key="1"
        >
          Accueil
        </div>
        <div
          className="left-bar-menu-element"
          onClick={(e) => toggleActive(e.target)}
          data-key="2"
        >
          Paroisses
        </div>
        <div
          className="left-bar-menu-element"
          onClick={(e) => toggleActive(e.target)}
          data-key="3"
        >
          Évènements
        </div>
      </div>

      <div className="left-bar-footer">
        <img
          src={process.env.PUBLIC_URL + "/imgs/icons/logout.png"}
          alt={"logo"}
        />

        <p>Se Déconnecter</p>
      </div>
    </div>
  );
};

export default LeftBar;
