import React, { useEffect, useState } from "react";
import GoogleMapReact from "google-map-react";

import { getNearParoisses } from "../../actions/paroisse";

import { useDispatch, useSelector } from "react-redux";

import List from "./List/List";
import Marker from "./Marker";

import mapStyles from "./mapStyles";

import { isEmpty } from "../utils";

const Map = () => {
  const [shistApi, setShiftApi] = useState(false);
  const [coordinates, setCoordinates] = useState({});
  let coords = "";
  const dispatch = useDispatch();
  const paroisses = useSelector((state) => state.nearParoisses);

  const apiKey = shistApi && "AIzaSyBwwRjHA3A4j64wjjtRgKfhviBkvz9psYE";

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      ({ coords: { latitude, longitude } }) => {
        setCoordinates({ lat: latitude, lng: longitude });
      }
    );
  }, []);

  useEffect(() => {
    coords = coordinates.lat + ";" + coordinates.lng;
    dispatch(getNearParoisses(coords));
  }, [coordinates]);

  return (
    <div className="map-container">
      <div className="map-list-container">
        <h1>Près de Chez vous</h1>
        {!isEmpty(paroisses.result) ? (
          <List paroisses={paroisses.result} />
        ) : (
          <>
            <h2>Aucun Résultat pour cette zone</h2>{" "}
          </>
        )}
      </div>
      <div className="map-container">
        <GoogleMapReact
          bootstrapURLKeys={{ key: apiKey }}
          defaultCenter={coordinates}
          center={coordinates}
          defaultZoom={14}
          margin={[50, 50, 50, 50]}
          options={{
            disableDefaultUI: true,
            zoomControl: true,
            styles: mapStyles,
          }}
          onChange={(e) => {
            console.log(e);
            setCoordinates({ lat: e.center.lat, lng: e.center.lng });
          }}
          yesIWantToUseGoogleMapApiInternals={true}
        >
              <Marker lat={coordinates.lat} lng={coordinates.lng} />

          {
            paroisses.result?.map((paroisses) => (
              <Marker lat={paroisses.location.coordinates[0]} lng={paroisses.location.coordinates[1]} />
            ))
          }
        </GoogleMapReact>
      </div>
    </div>
  );
};

export default Map;
