import React, { useEffect } from "react";
import { useParams, NavLink } from "react-router-dom";
import { getEvent } from "../actions/event";
import { useDispatch, useSelector } from "react-redux";
import { dateParser, timestampParser } from "../components/utils/index";

import dayjs from "dayjs";
import "dayjs/locale/fr"

const Event = () => {
  let { id } = useParams();
  const dispatch = useDispatch();

  const event = useSelector((state) => state.event);

  dayjs.locale('fr')
  console.log(dayjs(new Date()).format('dddd DD MMMM YYYY'));

  useEffect(() => {
    dispatch(getEvent(id));
  }, [id, dispatch]);
  return (
    <div className="event-container">
      <header className="event-header">
        <NavLink to="/">
          <img src="../imgs/icon.png" alt="logo" />
        </NavLink>
        <h1>Évènements</h1>
      </header>
      <div className="event-details-container">
        <img src={"." + event.poster} alt="event-poster" width="500px" />

        <div className="event-title">
          <p>{event.title}</p>
        </div>
        <div className="event-description">
          <p>{event.description}</p>
        </div>
        <div className="event-address">
          <p> Lieu: {event.address}</p>
        </div>

        <div className="event-sub-details">
          <div className="event-date">
              {event?.dateEvent[0] === event?.dateEvent[1] ? (
              <>
                <p>{dayjs(event.dateEvent[0]).format('dddd DD MMMM YYYY')}</p>
              </>
            ) : (
              <>
                <p>
                  {dateParser(event.dateEvent[0])} -{" "}
                  {dateParser(event.dateEvent[1])}
                </p>
              </>
            )}
          </div>
          <div className="event-time">
            <p>
              {timestampParser(event.startAt)} - {timestampParser(event.endAt)}
            </p>
          </div>
          <div className="event-organizer">
            <p>{event.organizer}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Event;
