import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllEvents } from "../actions/event";
import { NavLink } from "react-router-dom";
import { isEmpty } from "../components/utils";

import Loader from "../components/Loader/Loader";

const Events = () => {
  const [onLoading, setOnLoading] = useState(true);
  const [onPlaceholder, setOnPlaceholder] = useState(false);

  const dispatch = useDispatch();
  const events = useSelector((state) => state.events);

  useEffect(() => {
    if (onLoading) {
      dispatch(getAllEvents());
    }
  }, [onLoading]);

  useEffect(() => {
    if (!isEmpty(events)) {
      setOnLoading(false);
      events.map((event) => console.log(event));
    }

    setTimeout(() => {
      setOnLoading(false);
    }, 2000);
  }, [events]);

  return (
    <>
      {onLoading ? (
        <>
          <Loader />
        </>
      ) : (
        <>
          <div className="events-container">
            <header className="event-header">
              <NavLink to="/">
                <img src="../imgs/icon.png" alt="logo" />
              </NavLink>
              <h1>Évènements</h1>
            </header>

            <div className="events-blocks-container">
              {isEmpty(events)
                ? events.map((event) => (
                    <>
                      <div className="card">
                        <div className="card_load"></div>
                        <div className="card_load-content">
                          <div className="card_load_extreme_title"></div>
                          <div className="card_load_extreme_descripion"></div>
                        </div>
                      </div>
                    </>
                  ))
                : events.map((event) => (
                    <>
                      <div className="event-card">
                        <div className="event-poster"></div>
                        <div className="event-details">
                        <div className="event-title"></div>

                        </div>
                      </div>
                    </>
                  ))}
            </div>
          </div>
        </>
      )}
    </>
  );
};

export default Events;
