import React, { useEffect } from "react";

// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Autoplay, Keyboard, Pagination } from "swiper";

// Import Swiper styles
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";

// Import Redux Modules
import { useSelector, useDispatch } from "react-redux";

// Import events actions
import { getFirstThreeEvents } from "../../actions/event";

const Carousel = () => {
  const dispatch = useDispatch();
  const events = useSelector((state) => state.threeEvents);

  useEffect(() => {
    dispatch(getFirstThreeEvents());
    console.log("====================================");
    console.log(events);
    console.log("====================================");
  }, [events]);

  return (
    <div>
      <Swiper
        spaceBetween={30}
        centeredSlides={true}
        autoplay={{
          delay: 3000,
          disableOnInteraction: false,
        }}
        pagination={{
          clickable: true,
        }}
        navigation={true}
        keyboard={true}
        modules={[Autoplay, Pagination, Navigation, Keyboard]}
      >
        {events.map((event) => (
          <SwiperSlide>

          </SwiperSlide>
        ))}
        <SwiperSlide>
          <img
            src="./uploads/events/default-event.jpg"
            alt="event"
            width="1310vh"
          />
        </SwiperSlide>
        <SwiperSlide>
          <img
            src="./uploads/events/default-event.jpg"
            alt="event"
            width="1310vh"
          />
        </SwiperSlide>
        <SwiperSlide>
          <img
            src="./uploads/events/default-event.jpg"
            alt="event"
            width="1310vh"
          />
        </SwiperSlide>
      </Swiper>
    </div>
  );
};

export default Carousel;
