import React, { useEffect, useState } from "react";
import GoogleMapReact from "google-map-react";

import { getNearParoisses } from "../../actions/paroisse";

import { useDispatch } from "react-redux";

import List from "./List/List";

import mapStyles from "./mapStyles";

const Map = () => {
  const [coordinates, setCoordinates] = useState();
  const [coordsMap, setCoordsMap] = useState({
    coords: []
  })
  const dispatch = useDispatch();

  //const apiKey = "AIzaSyBwwRjHA3A4j64wjjtRgKfhviBkvz9psYE";

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      ({ coords: { latitude, longitude } }) => {
        setCoordinates({ lat: latitude, lng: longitude });
      }
    );

    setCoordsMap([coordinates.lat, coordinates.lng]);
  }, [coordinates]);

  const handleClick = () => {
    dispatch(getNearParoisses(coordsMap));
    console.log(coordsMap);
  };

  return (
    <div className="map-container">
      <div className="map-list-container">
        <h1>Pr√®s de Chez vous</h1>
        <List />
        <button onClick={handleClick}>Try</button>
      </div>
      <div className="map-container">
        <GoogleMapReact
          bootstrapURLKeys={{ key: "" }}
          defaultCenter={coordinates}
          center={coordinates}
          defaultZoom={14}
          margin={[50, 50, 50, 50]}
          options={{
            disableDefaultUI: true,
            zoomControl: true,
            styles: mapStyles,
          }}
          yesIWantToUseGoogleMapApiInternals={true}
        ></GoogleMapReact>
      </div>
    </div>
  );
};

export default Map;
