import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllEvents } from "../actions/event";
import { NavLink } from "react-router-dom";
import { isEmpty } from "../components/utils";

const Events = () => {
  const [onLoading, setOnLoading] = useState(true);

  const dispatch = useDispatch();
  const events = useSelector((state) => state.events);

  useEffect(() => {
    if (onLoading) {
      dispatch(getAllEvents());
    }
  }, [onLoading]);

  useEffect(() => {
    if (!isEmpty(events)) {
      setOnLoading(false);
      events.map((event) => console.log(event));
    }
  }, [events]);

  const cardLoadRender = () => {
    for (let index = 0; index < 5; index++) {
      return (
        <div class="card">
          <div class="card_load"></div>
          <div className="card_load-content">
            <div class="card_load_extreme_title"></div>
            <div class="card_load_extreme_descripion"></div>
          </div>
        </div>
      );
    }
  };

  return (
    <div className="events-container">
      <header className="event-header">
        <NavLink to="/">
          <img src="../imgs/icon.png" alt="logo" />
        </NavLink>
        <h1>Évènements</h1>
      </header>

      <div className="events-blocks-container">{cardLoadRender()}</div>
    </div>
  );
};

export default Events;
