import React, { useEffect, useState } from "react";
import { useParams, NavLink } from "react-router-dom";
import { getEvent } from "../actions/event";
import { useDispatch, useSelector } from "react-redux";
import {} from '../components/utils/index'

import dayjs from "dayjs";
import "dayjs/locale/fr";

const Event = () => {
  const event = useSelector((state) => state.event);

  const [loadEvent, setLoadEvent] = useState(true);
  let { id } = useParams();
  const dispatch = useDispatch();

  dayjs.locale("fr");
  const hourParser = (date) => {
    return dayjs(date).format("HH");
  };

  const minuteParser = (date) => {
    return dayjs(date).format("mm");
  };

  useEffect(() => {
    console.log(event);
    if (loadEvent) {
      dispatch(getEvent(id));
      setLoadEvent(false);
    }
  }, [id, loadEvent]);

  return (
    <div className="event-container">
      <header className="event-header">
        <NavLink to="/">
          <img src="../imgs/icon.png" alt="logo" />
        </NavLink>
        <h1>Évènements</h1>
      </header>
      <div className="event-details-container">
        <img src={"." + event.poster} alt="event-poster" width="500px" />

        <div className="event-title">
          <p>{event.title}</p>
        </div>
        <div className="event-description">
          <p>{event.description}</p>
        </div>
        <div className="event-address">
          <p> Lieu: {event.address}</p>
        </div>

        <div className="event-sub-details">
          <div className="event-date">
            {event?.dateEvent[0] === event?.dateEvent[1] ? (
              <>
                <p>{dayjs(event.dateEvent[0]).format("dddd DD MMMM YYYY")}</p>
              </>
            ) : (
              <>
                <p>
                  {dayjs(event.dateEvent[0]).format("dddd DD MMMM YYYY")} -{" "}
                  {dayjs(event.dateEvent[1]).format("dddd DD MMMM YYYY")}
                </p>
              </>
            )}
          </div>
          <div className="event-time">
            <p>
              {hourParser(event.startAt) +
                "H" +
                minuteParser(event.startAt) +
                "min"}{" "}
              -{" "}
              {hourParser(event.endAt) +
                "H" +
                minuteParser(event.endAt) +
                "min"}
            </p>
          </div>
          <div className="event-organizer">
            <p>{event.organizer}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Event;
